function multiplyBy11(n) {
  n = '0' + n.split('').reverse().join('') + '0';
  let result = [];
  let carry = 0;

  for (let i = 0; i < n.length - 1; i++) {
      let total = parseInt(n[i]) + parseInt(n[i + 1]) + carry;
      result.push(total % 10);
      carry = Math.floor(total / 10);
  }
  if (carry) {
      result.push(carry);
  }

  return result.reverse().join('');
}

console.log(multiplyBy11('113434'));
console.log(multiplyBy11('111111111'));
console.log(multiplyBy11('12345678987654321'));
console.log(multiplyBy11('9473745364784876253253263723'));
console.log(multiplyBy11(''));
